import java.util.Scanner;public class Main{public static void main(String[]args){Scanner s = new Scanner(System.in);int[]a=new int[10];for(int i=0;i<a.length;i++){a[i]=s.nextInt();}q(a,0,a.length-1);for(int i=a.length-1;i>6;i--){System.out.println(a[i]);}}static void q(int[]a,int l,int r){int L=l;int R=r;int x=a[(L+R)/2];do{while(a[L]<x)L++;while(a[R]>x)R--;if(L<=R)s(a,L++,R--);}while(L<=R);if(l<R)q(a,l,R);if(L<r)q(a,L,r);}static void s(int[]a,int i1,int i2){int t=a[i1];a[i1]=a[i2];a[i2]=t;}}
